import React, {useState} from 'react';
import './App.css';
import InputField from './components/InputField';
import Todo from './components/Todo';
import TodoList from './components/TodoList';

const App : React.FC = () => {
  const [todo, setTodo] = useState<string>('')
  const [todos, setTodos] = useState<Todo[]>([])
  const [completedTodos, setCompletedTodos] = useState<Todo[]>([])

  const handleAdd = (e: React.FormEvent) : void => {
    e.preventDefault()
    if(!todo)
      return;
    setTodos([...todos, {id: crypto.randomUUID(), name: todo, isDone: false}])
    setTodo('')
  }
  const completeTodo = (id: string) : void => {
    const task = todos.find((todo) => todo.id === id)
    console.log(`${task?.name} completed.`);
  }
  const cancelCompletion = (id: string) : void => {
    const task = completedTodos.find((todo) => todo.id === id)
    console.log(`${task?.name} uncompleted.`);
  }
  const deleteTodo = (id: string, list: Todo) : void => {
    const task = completedTodos.find((todo) => todo.id === id)
    console.log(`${task?.name} has been deleted.`);
  }
  const editTodo = (id: string, list: Todo) : void => {
    const task = completedTodos.find((todo) => todo.id === id)
    console.log(`${task?.name} is being edited.`);
  }

  return (
    <div className="App">
      <span className="heading">Taskify</span>
      <InputField todo={todo} setTodo={setTodo} handleAdd={handleAdd} />
      <TodoList todos={todos} setTodos={setTodos} />
    </div>
  );
}

export default App;
