import React, { FormEvent, useEffect, useRef, useState } from 'react'
import Todo from './Todo'
import {AiFillEdit, AiFillDelete} from 'react-icons/ai'
import {MdDone} from 'react-icons/md'

type Props = {
    todo: Todo
    toggleCompletion: (id: string) => void
    editTodo: (id: string, value: string) => void
    deleteTodo: (id: string) => void
}

const SingleTodo = ({todo, toggleCompletion, editTodo, deleteTodo}: Props) => {
  const [isEditing, setIsEditing] = useState<boolean>(false)
  const [edit, setEdit] = useState<string>(todo.name)

  const inputRef = useRef<HTMLInputElement>(null)
  useEffect(() => {
    inputRef.current?.focus()
  }, [isEditing])

  const handleEdit = () : void => {
    setIsEditing(!isEditing)
  }

  return (
    <form className='todos__single'>
        <span className="todos__single--text">
            {isEditing ? (
              <input
                value={edit}
                onChange={(e)=> setEdit(e.target.value)}
                className='todos__single--text'
                ref={inputRef}
              />
            ) : <span className='todos__single--text'>{todo.name}</span>
            }

        </span>
        <div>
            <span className='icon' onClick={handleEdit}><AiFillEdit /></span>
            <span className='icon' onClick={() => deleteTodo(todo.id)}><AiFillDelete /></span>
            <span className='icon' onClick={() => toggleCompletion(todo.id)}><MdDone /></span>
        </div>
    </form>
  )
}

export default SingleTodo
