import React from 'react'
import Todo from './Todo'
// import "./styles.css";
import SingleTodo from './SingleTodo';

interface Props {
    todos: Todo[]
    completedTodos: Todo[]
    setTodos: React.Dispatch<React.SetStateAction<Todo[]>>
    completeTodo: (id: string) => void
    cancelCompletion: (id: string) => void
    deleteTodo: (id: string, list: Todo[]) => void
    editTodo: (id: string) => void
}

const TodoList:React.FunctionComponent<Props> = ({
  todos, completedTodos, setTodos, completeTodo, cancelCompletion, deleteTodo, editTodo}: Props) => {
  const deleteUncompleted = (id: string) : void => deleteTodo(id, todos)
  const deleteCompleted = (id: string) : void => deleteTodo(id, completedTodos)
  return (
    <div className="container">
      <div className='todos'>
        <span className='todos__heading'>Active Tasks</span>
          {todos.map((todo:Todo) => {
            return(
              <SingleTodo key={todo.id} todo={todo} toggleCompletion={completeTodo} 
              deleteTodo={deleteUncompleted} editTodo={editTodo} />
            )
          })}
          {completedTodos.map((todo) => {

          })}
      </div>
    </div>
  )
}

export default TodoList
